option.value:
import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart, faComment, faEllipsis } from '@fortawesome/free-solid-svg-icons';
import Avatar from './ui/Avatar';
import Card from './ui/Card';

export interface CommentCardProps {
  id: string;
  author: {
    id: string;
    name: string;
    handle: string;
    avatar?: string;
    initials: string;
  };
  content: string;
  timestamp: string;
  likes: number;
  replies: number;
  isLiked?: boolean;
  onLike?: (id: string) => void;
  onReply?: (id: string) => void;
}

const CommentCard: React.FC<CommentCardProps> = ({
  id,
  author,
  content,
  timestamp,
  likes,
  replies,
  isLiked = false,
  onLike,
  onReply,
}) => {
  const [liked, setLiked] = React.useState(isLiked);
  const [likeCount, setLikeCount] = React.useState(likes);

  const handleLike = (e: React.MouseEvent) => {
    e.preventDefault();
    setLiked(!liked);
    setLikeCount(liked ? likeCount - 1 : likeCount + 1);
    onLike?.(id);
  };

  return (
    <Card className="p-3 sm:p-4 border-l-4 border-primary-500">
      <div className="flex gap-3">
        {/* Avatar */}
        <Avatar src={author.avatar} initials={author.initials} size="sm" />

        {/* Content */}
        <div className="flex-1 min-w-0">
          {/* Header */}
          <div className="flex items-center justify-between gap-2">
            <div className="flex items-center gap-2 min-w-0">
              <h4 className="font-semibold text-dark-900 dark:text-light-50 truncate">
                {author.name}
              </h4>
              <span className="text-dark-500 dark:text-dark-400 text-sm truncate">
                @{author.handle}
              </span>
              <span className="text-dark-400 dark:text-dark-500 text-sm">
                {timestamp}
              </span>
            </div>
            <button className="text-dark-400 hover:text-dark-600 dark:hover:text-dark-200 flex-shrink-0">
              <FontAwesomeIcon icon={faEllipsis} className="w-4 h-4" />
            </button>
          </div>

          {/* Comment Text */}
          <p className="text-dark-900 dark:text-light-50 mt-2 text-sm leading-relaxed">
            {content}
          </p>

          {/* Actions */}
          <div className="flex items-center gap-4 mt-3 text-dark-500 dark:text-dark-400 text-sm">
            <button
              onClick={(e) => {
                e.preventDefault();
                onReply?.(id);
              }}
              className="flex items-center gap-1 hover:text-primary-600 dark:hover:text-primary-400 transition-colors"
            >
              <FontAwesomeIcon icon={faComment} className="w-3 h-3" />
              {replies > 0 && <span>{replies}</span>}
            </button>

            <button
              onClick={handleLike}
              className={`flex items-center gap-1 transition-colors ${
                liked
                  ? 'text-red-600 dark:text-red-400'
                  : 'hover:text-red-600 dark:hover:text-red-400'
              }`}
            >
              <FontAwesomeIcon
                icon={faHeart}
                className="w-3 h-3"
                style={{ fill: liked ? 'currentColor' : 'none' }}
              />
              {likeCount > 0 && <span>{likeCount}</span>}
            </button>
          </div>
        </div>
      </div>
    </Card>
  );
};

export default CommentCard;

